import groovy.json.JsonSlurper
//script to read access token
def response = messageExchange.response.responseContent
def obj = new JsonSlurper().parseText(response)
def accesstoken= obj.data.token_type+ " "+obj.data.access_token
log.info accesstoken
//to store it into test suite level custom property
context.testCase.testSuite.setPropertyValue("accesstoken",accesstoken) 
 def headers =  messageExchange.getResponseHeaders()
log.info headers["#status#"]
    if(headers["#status#"].equals(['HTTP/1.1 200 OK']))
    {
        log.info "status is 200"
    }
    else
    {
        log.info "something went wrong"
    }
    assert ['HTTP/1.1 200 OK'] == headers["#status#"]

