con:configuration.scriptText:
import groovy.json.JsonSlurper
def response = messageExchange.response.responseContent
def obj = new JsonSlurper().parseText(response)
//Product ID and qunatity updated details
productid_exparr = [1637,1647]
def productid_exp_count = productid_exparr.size()
log.info productid_exp_count
quantityupdated_arr = [55,105]

//to get the size of an product ID array
def count = obj.data.id.size()
log.info count

for(i=0;i<productid_exp_count;i++)
{
	productid_exp = productid_exparr[i]
	quantity_exp = quantityupdated_arr[i]
	def productfoundflag = false
	def quantityupdatedflag = false
	for(j=0;j<count;j++)
	{
		if(productid_exp==obj.data.id[j])
		{
			log.info "product id $productid_exp is found"
			productfoundflag = true
			if (quantity_exp == obj.data.quantity[j]){
				log.info "product quantity - $quantity_exp updated correctly and verified"
				quantityupdatedflag=true
				break;
			}

		}
	}
	if (productfoundflag==false)
	{
		log.info "product id $productid_exp is not found"
	}
	else if(quantityupdatedflag == false)
	{
		log.info "product quantity - $quantity_exp update - failed"
	}
}

