import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status

// get TestCase to generate access token and login
def tc = testRunner.testCase.testSuite.project.testSuites["EndToEndFunctionalTesting"].testCases["GetAccessTokenandLogin"]
// run test synchrouously
def runner = tc.run( null, false )

def filepath = "C:/Users/RamaniPalaniappan/Desktop/SDET_B1_API/ProductDetails.xls"

File excel = new File(filepath)

//to fetch workbook
Workbook wb = Workbook.getWorkbook(excel)

//to fetch sheet
Sheet sh=wb.getSheet("Sheet1")

//to fetch number of rows and columns
def rowsize = sh.getRows().toInteger()
def columnsize = sh.getColumns().toInteger()
log.info "$rowsize and $columnsize"

//to fetch the value in the variable
for(def i=1;i<rowsize;i++)
{
	for(def j=0;j<columnsize;j++)
	{
		if(j==0)
		{
			model = sh.getCell(j,i).getContents()
			log.info "$model"
		}
		else if(j==1)
		{
			quantity = sh.getCell(j,i).getContents()
			log.info "$quantity"
		}
		else if(j==2)
		{
			price = sh.getCell(j,i).getContents()
			log.info "$price"
		}
		else if(j==3)
		{
			name = sh.getCell(j,i).getContents()
			log.info "$name"
		}
		else
		{
			metatitle = sh.getCell(j,i).getContents()
			log.info "$metatitle"
		}
	}

	// get TestCase
	def tc1 = testRunner.testCase.testSuite.project.testSuites["EndToEndFunctionalTesting"].testCases["TC025_AddandDeleteProduct FunctionalTesting"]

	// set product details properties from the script itself
	tc1.testSteps["ProductDetails"].setPropertyValue("model", model )
	tc1.testSteps["ProductDetails"].setPropertyValue("quantity", quantity )
	tc1.testSteps["ProductDetails"].setPropertyValue("price", price )
	tc1.testSteps["ProductDetails"].setPropertyValue("name", name )
	tc1.testSteps["ProductDetails"].setPropertyValue("meta_title", metatitle )

	// run test synchrouously
	def runner1 = tc1.run( null, false )

	// show the test case result status
	log.info "Status: $runner1.status, time taken for TestCase was: $runner1.timeTaken ms"
	log.info "Number of test steps which ran: " + runner1.results.size()

	// assert that it didn't fail
	assert runner1.status != Status.FAILED : runner1.reason

}
// get TestCase to logout admin user
def tc2 = testRunner.testCase.testSuite.project.testSuites["EndToEndFunctionalTesting"].testCases["LogoutAdminUser"]
// run test synchrouously
def runner2 = tc2.run( null, false )
log.info "Number of test steps which ran: " + runner2.results.size()

import groovy.sql.Sql;
import com.eviware.soapui.model.testsuite.TestRunner.Status

// get TestCase to generate access token and login
def tc = testRunner.testCase.testSuite.project.testSuites["EndToEndFunctionalTesting"].testCases["GetAccessTokenandLogin"]
// run test synchrouously
def runner = tc.run( null, false )

def url = "jdbc:mysql://localhost:3306/RESTDB";

def user ="root"
def password = "root"
def driver = "com.mysql.jdbc.Driver"
//Register Maria DB
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)
//Connect to sql
def sql = Sql.newInstance(url,user,password,driver)

sql.eachRow("select * from TC026")
{
    row->
    def name = "$row.name";
    log.info name

//	row->
    def metatitle = "$row.metatitle";
    log.info metatitle

		// get TestCase
	def tc1 = testRunner.testCase.testSuite.project.testSuites["EndToEndFunctionalTesting"].testCases["TC026_AddNewCategoryandProduct FunctionalTesting"]

	// set product details properties from the script itself
	tc1.testSteps["ProductCategoryProperties"].setPropertyValue("name", name )
	tc1.testSteps["ProductCategoryProperties"].setPropertyValue("metatitle", metatitle )

	// run test synchrouously
	def runner1 = tc1.run( null, false )

	// show the test case result status
	log.info "Status: $runner1.status, time taken for TestCase was: $runner1.timeTaken ms"
	log.info "Number of test steps which ran: " + runner1.results.size()

	// assert that it didn't fail
	assert runner1.status != Status.FAILED : runner1.reason

}
// get TestCase to logout admin user
def tc2 = testRunner.testCase.testSuite.project.testSuites["EndToEndFunctionalTesting"].testCases["LogoutAdminUser"]
